DROP TABLE IF EXISTS park;
CREATE TABLE park(
   uid            INTEGER NOT NULL PRIMARY KEY 
  ,url            VARCHAR
  ,fullName       VARCHAR
  ,parkCode       VARCHAR UNIQUE NOT NULL
  ,description    VARCHAR
  ,latitude       NUMERIC
  ,longitude      NUMERIC
  ,directionsInfo VARCHAR
  ,directionsUrl  VARCHAR
  ,weatherInfo    VARCHAR
  ,name           VARCHAR
  ,designation    VARCHAR
  ,states         VARCHAR
  ,activities     VARCHAR
  ,topics         VARCHAR
  ,entranceFees   VARCHAR
  ,entrancePasses VARCHAR
  ,addresses      VARCHAR
  ,phoneNumbers   VARCHAR
  ,emailAddresses INTEGER 
  ,images         VARCHAR
);
